  //   });

  //   // return this.state.score;
  // }

  render() {
    const { score } = this.props;

    return (
      <div>
        {'Placar: '}
        <span data-testid="header-score">
          {score.score}
        </span>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  score: state.sectionReducer.section,
});

export default connect(mapStateToProps)(ScoreBoard);
