import React, { Component } from 'react';
import WrongAnswer from './WrongAnswer';
import CorrectAnswer from './CorrectAnswer';

class AlternativesContainer extends Component {
  render () {
    const { alternatives, styleAlternative, countDown, answerClick } = this.props;
    let a = undefined;

    renderAlternatives = (alternatives) => {
      alternatives.map((text, alternativeIndex, array) => {
        if (text === array[array.length - 1]) {
          return (<CorrectAnswer
            key={text}
            text={text}
            styleAlternative={styleAlternative}
            answerClick={answerClick}
            countDown={countDown}
          />);
        }
        return (<WrongAnswer
          key={text}
          text={text}
          alternativeIndex={alternativeIndex}
          styleAlternative={styleAlternative}
          answerClick={answerClick}
          countDown={countDown}
        />);
      });
    };

    return (
      <>
        { this.renderAlternatives(alternatives).sort() }
      </>
    );
  }
}

export default AlternativesContainer;
