import React, { Component } from 'react';
import { connect } from 'react-redux';
import { questionsRequest } from '../actions';

class Questions extends Component {
  constructor() {
    super();
    this.state = {
      questionIndex: 0,
      isLoad: false,
      questions: [],
    };
  }

  async componentDidMount() {
    const { requestQuestions } = this.props;
    const token = localStorage.getItem('token');

    await requestQuestions(token);

    const { questions } = this.props;
    const { results } = questions;

    handleState(results);
  }

  handleState(results) {
    this.setState({
      questions: results,
    });
  }

  render() {

    // const questionList = questions ? results : [];
    // const listLength = 5;

    // return questionList !== listLength ? 'Loading' : (
  return (
      <div>
        { questionList.filter((question, index) => {
          if (questionIndex === index) {
            return (
              <div>
                <p data-testid="question-text">{ question.name }</p>
                <p data-testid="question-category">{question.category}</p>
              </div>
            );
          }
        })}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  questions: state.triviaReducer.questions,
});

const mapDispatchToProps = (dispatch) => ({
  requestQuestions: (token) => dispatch(questionsRequest(token)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Questions);
