  // }

  async componentDidMount() {
    const { requestQuestions } = this.props;
    const token = localStorage.getItem('token');
    await requestQuestions(token);
    const { questions } = this.props;
    // this.setState({
    //   questions,
    // });
  }

  render() {
    return (

      <div>
        {/* { questions.results && questions.results.map(({ category }) => (
          // <p data-testid="question-category">Categoria da pergunta</p>
          // <p data-testid="question-text">Texto da pergunta</p>
          <p>{category}</p>
        ))} */}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  questions: state.triviaReducer.questions,
});

const mapDispatchToProps = (dispatch) => ({
  requestQuestions: (token) => dispatch(questionsRequest(token)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Questions);
