// create react component
import React, { Component } from 'react';
import logo from '../trivia.png';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
    };
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <div>
        <img src={ logo } className="App-logo" alt="logo" width="150" />
        <p>
          SUA VEZ
        </p>
        <h1>Login</h1>
        <label htmlFor="inputEmail">
          <input type="text" placeholder="Email" id="inputEmail" data-testid="input-gravatar-email" name="email" />
        </label>
        <label htmlFor="inputName">
          <input type="text" placeholder="Nome" id="inputName" data-testid="input-player-name" name="name" />
        </label>
        <button type="button" data-testid="btn-play">Jogar</button>
      </div>

    );
  }
}

export default Login;
