import React, { Component } from 'react';
import WrongAnswer from './WrongAnswer';
import CorrectAnswer from './CorrectAnswer';

class AlternativesContainer extends Component {
  render() {
    const { alternatives } = this.props;
    return (
      <>
        {
          alternatives.map((text, alternativeIndex) => {
            if (text === correctAnswer) {
              return (<CorrectAnswer
                key={text}
                text={text}
                styleAlternative={styleAlternative}
                answerClick={this.answerClick}
                countDown={countDown}
              />);
            }
            return (<WrongAnswer
              key={text}
              text={text}
              alternativeIndex={alternativeIndex}
              styleAlternative={styleAlternative}
              answerClick={this.answerClick}
              countDown={countDown}
            />);
          }).sort()
        }
      </>
    );
  }
}

export default AlternativesContainer;
