import React, { Component } from 'react';
import { connect } from 'react-redux';
import { questionsRequest } from '../actions';

class Questions extends Component {
  constructor() {
    super();
    this.state = {
      questionIndex: 0,
      isLoad: false,
      questions: [],
    };
    this.handleState = this.handleState.bind(this);
  }

  async componentDidMount () {
    const { requestQuestions } = this.props;
    const token = localStorage.getItem('token');

    await requestQuestions(token);

    const { questions } = this.props;
    const { results } = questions;

    this.handleState(questions);
  }

  handleState(questions) {
    this.setState({
      questions: questions.results,
    });
  }

  render() {
    const { questions, questionIndex } = this.state;
    console.log(questions);
    // const questionList = questions ? results : [];
    // const listLength = 5;
    const list = [...questions];
    // return questionList !== listLength ? 'Loading' : (
    return (
      <div>
        {list.filter((question, index) => {
          if (questionIndex === index) {
            return (
              <div>
                <p data-testid="question-text">{question.name}</p>
                <p data-testid="question-category">{question.category}</p>
              </div>
            );
          }
        })}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  questions: state.triviaReducer.questions,
});

const mapDispatchToProps = (dispatch) => ({
  requestQuestions: (token) => dispatch(questionsRequest(token)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Questions);
