// create a reducer to manage the login state
import { SECTION_USER } from '../actions/actionTypes';

const INITIAL_STATE = {
  section: {},
};

const sectionReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SECTION_USER:
    return {
      ...state, section: action.section,
    };
  default:
    return state;
  }
};

export default sectionReducer;
